import CartActionTypes from './cart.actiontypes';

const INITIAL_STATE = {
    hidden: true,
}

const cartReducer = (currentState = INITIAL_STATE, action) => {
    switch(action.type) {
        case CartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...currentState,
                hidden: !currentState.hiddden
            }
        default:
            return currentState
    }
}

export default cartReducer;
